<?php class ModelSuperseoboxindex extends Model { private $ssb_data;private $ssb_helper;private $total=0;private $gen_pack_number=0;private $totalProdFromCategory=array();function __construct(){ global $registry;parent::__construct($registry);require_once DIR_CONFIG.'ssb_library/ssb_data.php';$this->ssb_data=ssb_data::getInstance();require_once DIR_CONFIG.'ssb_library/ssb_helper.php';$this->ssb_helper=ssb_helper::getInstance();} public function getCPBI_item($CPBI,$id_item){ $data_CPBI_item=$this->{'get_'.$CPBI}('',$id_item);return $data_CPBI_item;} public function getCPBI($CPBI,$categoryEntity='',$gen_pack_number=0,$internal_entity=''){ $data_CPBI=array();$time_start=microtime(true);$this->gen_pack_number=$gen_pack_number;if($CPBI=='CPBI_urls' AND $internal_entity==''){ $CPBI='all';}elseif($internal_entity !=''){ $CPBI=$internal_entity;} if($CPBI=='all'){ if($categoryEntity=='' OR $categoryEntity=='urls'){ $CPBIs=$this->ssb_data->getMatadata('CPBI',array('keys'=>true));}else{ $MD_entities=$this->ssb_data->getMatadata('Entites');$CPBIs=array_keys($MD_entities[$categoryEntity]);} $total=0;foreach($CPBIs as $CPBI_){ $data=$this->getCPBI($CPBI_,$categoryEntity,$gen_pack_number);$total+=$data['total'];$data_CPBI['data'][$CPBI_]=$data['data'];} $data_CPBI['total']=$total;$data_CPBI['name']='all';}else{ $gen_pack=$this->ssb_data->getGenPack($gen_pack_number,$CPBI,$categoryEntity);if($CPBI=='related_prod' OR $CPBI=='images' OR $categoryEntity=='images' OR $categoryEntity=='reviews'){ $CPBI='product';} $data_CPBI=$this->{'get_'.$CPBI}($gen_pack);} $this->ssb_data->getSpecialPatterns();return $data_CPBI;} private function get_product($gen_limit='',$item_id=''){ $languages=$this->ssb_helper->getLanguages();$this->total=0;$respond=array();foreach ($languages as $l_code=> $l_data){ $respond_lang=$this->get_product_lang($l_data,$gen_limit,$item_id);$respond =array_replace_recursive ($respond,$respond_lang);} return array('data'=> $respond,'total'=> $this->total,'name'=> 'product');} private function get_product_lang($l_data,$gen_limit,$item_id){ $l_code=$l_data['code'];$l_id =$l_data['language_id'];$sql="SELECT pd.title_image, pd.alt_image, pd.tag, pd.meta_description, pd.meta_keyword, pd.seo_title, pd.seo_h1, p.product_id, p.tax_class_id, p.price, p.model, m.name as b_name, p.sku, p.upc, pd.name, p.image, pd.description, md.description as b_description FROM ".DB_PREFIX."product_description pd    INNER JOIN ".DB_PREFIX."product p on pd.product_id = p.product_id     LEFT  JOIN ".DB_PREFIX."manufacturer m on m.manufacturer_id = p.manufacturer_id    LEFT  JOIN ".DB_PREFIX."manufacturer_description md on m.manufacturer_id = md.manufacturer_id and md.language_id = ".$l_id ." WHERE  pd.language_id = ".$l_id;if($item_id !==''){ $sql.=" AND p.product_id =".$item_id.";";}else{ $sql.=" LIMIT ".(int)$gen_limit['start'].",".(int)$gen_limit['limit'].";";} $query=$this->db->query($sql);$products=array();foreach($query->rows as $product){ if(!isset($products [$product['product_id']][$l_code])){ $sql="SELECT cd.category_id as c_id, cd.name as c_name, cd.description as c_description FROM ".DB_PREFIX."product_to_category pc       INNER  JOIN ".DB_PREFIX."category_description cd on cd.category_id = pc.category_id and cd.language_id = ".(int)$l_data['language_id']."  WHERE pc.product_id = '" . $product['product_id'] . "';";$query_cat=$this->db->query($sql);$categories=array('cn'=> array(),'c_id' => array(),'cd'=> array());foreach($query_cat->rows as $category){ $categories['cn'][] =$category['c_name'];$categories['c_id'][] =$category['c_id'];$categories['cd'][] =$category['c_description'];} $products [$product['product_id']][$l_code]=array( 'pn' => $product['name'],'pd' => $product['description'],'cn' => $categories['cn'],'c_id' => $categories['c_id'],'cd' => $categories['cd'],'bn' => $product['b_name'],'bd' => $product['b_description'],'pp' => $this->currency->format($product['price']),'pm' => $product['model'],'ps' => $product['sku'],'pu' => $product['upc'],