ategory[$category['category_id']]=$query_total->rows[0]['totalProd'];if($this->totalProdFromCategory[$category['category_id']]==0){ $childrenCategory=array();$query_children=$this->db->query("SELECT category_id FROM ".DB_PREFIX."category WHERE parent_id = '" . (int)$category['category_id'] . "'");foreach ($query_children->rows as $category_children) { $sql_total_prod="SELECT COUNT(product_id) as totalProd FROM ".DB_PREFIX."product_to_category WHERE category_id = '". (int)$category_children['category_id'] ."';";$query_total=$this->db->query($sql_total_prod);$this->totalProdFromCategory[$category['category_id']]+=$query_total->rows[0]['totalProd'];} } } $query_children=isset($query_children) ? $query_children:$this->db->query("SELECT category_id FROM ".DB_PREFIX."category WHERE parent_id = '" . (int)$category['category_id'] . "'");$result_csd=array('cn'=> array(),'c_id' => array(),'cd'=> array());foreach ($query_children->rows as $category_children) { $sql="SELECT category_id, name, description FROM ".DB_PREFIX."category_description  WHERE category_id = ".(int)$category_children['category_id']." AND language_id = ".$l_id;$query_csd=$this->db->query($sql);if(isset($query_csd->rows[0])){ $result_csd['cn'][] =$query_csd->rows[0]['name'];$result_csd['c_id'][] =$query_csd->rows[0]['category_id'];$result_csd['cd'][] =$query_csd->rows[0]['description'];} } $ep_data=array( 'totalProduct' => $this->totalProdFromCategory[$category['category_id']],'CPBI' => 'category','id' => $category['category_id'],'l_id' => $l_id );$exampleProducts=$this->getExampleProducts($ep_data);$categories [$category['category_id']][$l_code]=array( 'cn' => $category['name'],'cd'=> $category['description'],'cp'=> !is_null($category['parent_name']) ? $category['parent_name']:'','csn'=> $result_csd['cn'],'csn_id'=> $result_csd['c_id'],'csd'=> $result_csd['cd'],'ep'=> $exampleProducts,'tp'=> $this->totalProdFromCategory[$category['category_id']],'image'=> $category['c_image'],'seo_field' => array( 'name' =>