private function insertInHeader($html){ $this->page=str_replace_once('</head>',$html.'  </head>',$this->page);} private function insertInBody($html){ $this->page=str_replace_once('</body>',$html.'  </body>',$this->page);} private function clearString($text,$full=false,$html_ch=false){ $clear_char=array( 'from' => array('\n','\r','\\','"', '_'),     'to' => array('', '', ' ', '\'', ' ')    );    if($full){     $text = $this->db->escape(html_entity_decode($text, ENT_COMPAT, "UTF-8"));    }    if($html_ch){     $text = strip_tags($text);    }    $text = trim(str_replace($clear_char['from'], $clear_char['to'], $text));    return $text;   }   private function setCustom_reviews() {       $this->load->model('catalog/review');       if (isset($this->request->get['page'])) {        $page = $this->request->get['page'];       } else {        $page = 1;       }       $results = $this->model_catalog_review->getReviewsByProductId($this->data['product_id'], ($page - 1) * 5, 5);       $this->data['custom_reviews'] = array();       foreach ($results as $result) {        $this->data['custom_reviews'][] = array(         'author'     => $result['author'],         'text'       => $result['text'],         'rating'     => (int)$result['rating'],         'date_added' => date($this->language->get('date_format_short'), strtotime($result['date_added']))        );       }   }       private function getSentences($text , $count) {    $text = strip_tags(html_entity_decode($text));    $pos = 0;    $found = false;    for ($x=0; $x<$count; $x++)    {      $pos = strpos($text, '.', $pos);     if($pos !== false){      $pos++;      $found = true;     }else{      break;     }    }    if(!$found OR $pos === false){     $respond = $text;    }else{     $respond = substr($text, 0, $pos);    }    return $respond;   }  }  function str_replace_once($search, $replace, $text)   {      $pos = strpos($text, $search);      return $pos!==false ? substr_replace($text, $replace, $pos, strlen($search)) : $text;   }  ?>
