private function saveMinifyConfig($post) {
	$output = '<' . '?php' . "\n";
	$output .= '////////////////////////////////////////////////////////////////////////////////////////////' . "\r\n";
	$output .= '//    AUTOMATICALLY GENERATED CONFIGURATION FILE FOR MINIFICATION PROCESSING LIBRARIES    //' . "\r\n";
	$output .= '////////////////////////////////////////////////////////////////////////////////////////////' . "\r\n";
	$output .= '$min_encodeImages           = ' . (($post['config_minify_encode_images']) ? 'true' : 'false') . ';' . "\r\n";
	$output .= '$min_encodeImageSize        = ' . (($post['config_minify_image_size']) ? $post['config_minify_image_size'] : '3072') . ';' . "\r\n";
	$output .= '$min_encodeImageOccurs      = ' . (($post['config_minify_image_occurs']) ? $post['config_minify_image_occurs'] : '3') . ';' . "\r\n";
	$output .= '$min_encodeURL              = ' . (($post['config_minify_encode_url']) ? 'true' : 'false') . ';' . "\r\n";
	$output .= '$min_errorLogger            = ' . (($post['config_minify_logging']) ? 'true' : 'false') . ';' . "\r\n";
	$output .= '$min_allowDebugFlag         = ' . (($post['config_minify_debug_mode']) ? 'true' : 'false') . ';' . "\r\n";
	$output .= '$min_serveOptions[\'maxAge\'] = ' . (($post['config_minify_max_age']) ? $post['config_minify_max_age'] : '86400') . ';' . "\r\n";
	$output .= '$min_documentRoot           = \'' . (($post['config_minify_file_path']) ? $post['config_minify_file_path'] : '') . '\';' . "\r\n";
	$output .= '$min_cacheFileLocking       = ' . $post['config_minify_file_locking'] . ';' . "\r\n";
	if (isset($post['config_minify_storage']) && $post['config_minify_storage']) {
		$output .= 'require dirname(__FILE__) . \'/min/Minify/Cache/APC.php\';' . "\r\n";
		$output .= '$min_cachePath = new Minify_Cache_APC();' . "\r\n";
	} else {
		$output .= '$min_cachePath      = \'../system/cache/min\';' . "\r\n";
	}
	$output .= '?>' . "\r\n";

	$file = fopen(DIR_SYSTEM . '../min/min.config', 'w');
	fwrite($file, $output);
	fclose($file);
}

private function loadSetting($setting, $default = 0) {
	return isset($this->request->post[$setting]) ? $this->request->post[$setting] : (($this->config->get($setting)) ? $this->config->get($setting) : $default);
}

